/***** Drive workspace bootstrap for Shellpoint SII/RFI (Full File) *****/
/* Folder: https://drive.google.com/drive/folders/1XSA8_dB-He8ee9zEfVrkpZX99WTOMfrM */

const PARENT_FOLDER_ID = '1XSA8_dB-He8ee9zEfVrkpZX99WTOMfrM';
const TZ = 'America/Los_Angeles';

function main() {
  try {
    const parent = DriveApp.getFolderById(PARENT_FOLDER_ID);

    // 1) Ensure subfolders
    const subs = ['01_Source_Documents','02_WIP_Documents','03_Final_Output','ZZ_Proof_Artifacts']
      .reduce((acc, n) => { acc[n] = ensureSubfolder_(parent, n); return acc; }, {});

    // 2) Create/overwrite WIP Docs with full content
    const coverDoc = ensureDocWithContent_(subs['02_WIP_Documents'],
      'Cover Letter_SII-RFI_Shellpoint_Loan 0695281006_(Eric B. Jones)_Draft',
      coverLetterFull_());

    const emailDoc = ensureDocWithContent_(subs['02_WIP_Documents'],
      'Email_Draft_to_Shellpoint_loanservicing@newrez.com',
      emailFull_());

    const indexDoc = ensureDocWithContent_(subs['02_WIP_Documents'],
      'Packet_Index_and_Assembly_Checklist',
      packetIndexFull_());

    const contactIndexDoc = ensureDocWithContent_(subs['02_WIP_Documents'],
      'Contact_and_Document_Index_Page',
      contactIndexFull_());

    // 3) Proof log sheet
    const proofSheet = ensureProofLog_(subs['ZZ_Proof_Artifacts'], parent.getName());

    // 4) Upload request in Source
    const readmeDoc = ensureDocWithContent_(subs['01_Source_Documents'],
      'UPLOAD_REQUEST_ReadMe',
      uploadReadme_());

    // 5) Log all links
    logLinks_(parent, {
      ...subs,
      coverDoc, emailDoc, indexDoc, contactIndexDoc, readmeDoc, proofSheet
    });

  } catch (e) {
    console.error('Fatal error in main(): ' + e.stack || e);
    throw e;
  }
}

/******** helpers ********/

function ensureSubfolder_(parent, name) {
  const it = parent.getFoldersByName(name);
  return it.hasNext() ? it.next() : parent.createFolder(name);
}

/**
 * Ensures a Google Doc with the exact name exists in the given folder and sets its content.
 * If a file with that name exists but is NOT a Google Doc (e.g., PDF), a fresh Doc is created anyway.
 * Returns the Document object.
 */
function ensureDocWithContent_(folder, name, content) {
  // Find any file with this name inside the target folder
  const it = folder.getFilesByName(name);
  let doc;

  if (it.hasNext()) {
    const file = it.next();
    if (file.getMimeType() === MimeType.GOOGLE_DOCS) {
      // Open as Doc
      doc = DocumentApp.openById(file.getId());
    } else {
      // Name collision with non-Doc; create a new Doc (Drive allows duplicate names)
      const newDoc = DocumentApp.create(name);
      DriveApp.getFileById(newDoc.getId()).moveTo(folder);
      doc = newDoc;
    }
  } else {
    // Create brand new Doc
    const newDoc = DocumentApp.create(name);
    DriveApp.getFileById(newDoc.getId()).moveTo(folder);
    doc = newDoc;
  }

  // Write content (overwrite)
  const body = doc.getBody();
  body.clear();
  body.setText(content);
  doc.saveAndClose();
  return doc;
}

/**
 * Ensures a proof log Google Sheet exists; if not, creates and seeds headers + first row.
 * Returns the Spreadsheet object.
 */
function ensureProofLog_(folder, topName) {
  const name = 'Proof_Log_Shellpoint_0695281006';
  const it = folder.getFilesByName(name);
  if (it.hasNext()) {
    return SpreadsheetApp.openById(it.next().getId());
  }
  const ss = SpreadsheetApp.create(name);
  DriveApp.getFileById(ss.getId()).moveTo(folder);
  const sh = ss.getActiveSheet();
  sh.getRange(1,1,1,7).setValues([[
    'Timestamp (PT)','Action','File Name / Link','Location (Folder)','Notes / Agent Name','Case/Ref #','Next Step Due (PT)'
  ]]);
  sh.getRange(2,1,1,7).setValues([[
    Utilities.formatDate(new Date(), TZ, 'yyyy-MM-dd HH:mm:ss'),
    'Workspace initialized', '', topName, 'Subfolders + Docs created', '', ''
  ]]);
  return ss;
}

/**
 * Logs URLs for quick copy from View → Logs.
 */
function logLinks_(parent, all) {
  console.log('Top folder: ' + parent.getUrl());
  Object.keys(all).forEach(k => {
    const v = all[k];
    try {
      if (v && v.getUrl) {
        console.log(k + ': ' + v.getUrl());
      } else if (v && v.getId) {
        console.log(k + ': https://drive.google.com/open?id=' + v.getId());
      }
    } catch (e) {
      // Some are plain folders (handled), others are Docs/Sheets; ignore if any lack getUrl
    }
  });
}

/******** content ********/

